mapper.update:
update order_management
set state=0
where id = #{id}

update order_management
order_num=#{orderNum}, customer_name=#{customerName}, material_name=#{materialName}, size=#{size}, order_amount=#{orderAmount}, unit_price=#{unitPrice}, total_price=#{totalPrice}, state=#{state}, discription=#{discription}, update_time=#{updateTime}
where id=#{id}
